mapper.insert:
INSERT INTO pet_diary_reply (
    diary_id, user_id, content, parent_id
) VALUES (
    #{diaryId}, #{userId}, #{content}, #{parentId}
)

mapper.delete:
DELETE FROM pet_diary_reply
WHERE id = #{id}
OR parent_id = #{id}

mapper.update:
UPDATE pet_diary_reply
SET content = #{content}
WHERE id = #{id}

UPDATE pet_diary
SET reply_count = reply_count + #{count}
WHERE id = #{diaryId}
